        return new Source("{% extends 'base.html.twig' %}

{% block title %}Promotion index{% endblock %}

{% block content %}
    <br> <br> <br> <br>
    <h1>Promotion index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Nom</th>
                <th>Description</th>
                <th>Type</th>
                <th>Reduction</th>
                <th>Nom de l'hotel</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for promotion in promotions %}
            <tr>
                <td>{{ promotion.Nom }}</td>
                <td>{{ promotion.description }}</td>
                <td>{{ promotion.type }}</td>
                <td>{{ promotion.Reduction }}</td>
                <td>{{ promotion.hotel.Nom }}</td>
                <td>
                    <a class=\"btn btn-style btn-primary\" href=\"{{ path('promotion_show', {'id': promotion.id}) }}\">show</a>
                    <a class=\"btn btn-style btn-primary\" href=\"{{ path('promotion_edit', {'id': promotion.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a class=\"btn btn-style btn-primary\" href=\"{{ path('promotion_new') }}\">Create new</a>
{% endblock %}
